import React from 'react'
import {
  Link
} from 'react-router-dom';

import Container from 'react-bootstrap/Container';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';
import Table from 'react-bootstrap/Table';
import Button from 'react-bootstrap/Button';

import Masonry, {ResponsiveMasonry} from 'react-responsive-masonry'

import Add from '../components/Add';


export default function Main({ list, addItemFunction, setAddForm, deleteItemFunction }) {

  const editNote = () => {

  };

  return (
    <Container fluid>
      <Row>
        <Col>
          <Add addItemFunction={ addItemFunction } setAddForm={ setAddForm } />
        </Col>
      </Row>
      <Row>
        <Col>
            <ResponsiveMasonry
              columnsCountBreakPoints={{ 200: 1, 600: 2, 950: 3, 1300: 4 }}>
              <Masonry>
                {list.map(item => (
                  <div key={ item.todo_id } className="p-3">
                    <Container fluid className="p-4 bg-warning rounded-4">
                      <Row>
                        <Col>
                          <h5 className="fw-bold">{ item.todo_id }</h5>
                        </Col>
                        <Col className="text-end" xs={ 'auto' }>
                          <Link className="fw-bold text-decoration-none" as={Button} onClick={ () => deleteItemFunction(item.todo_id) }>
                            X
                          </Link>
                        </Col>
                      </Row>
                      <Row className="px-3">
                        <Col>
                          <div style={{ whiteSpace: 'pre-wrap' }}>
                            { item.description }
                          </div>
                        </Col>
                      </Row>
                    </Container>
                  </div>
                ))}
              </Masonry>
            </ResponsiveMasonry>
        </Col>
      </Row>
    </Container>
  )
}

