import './App.css';

import React, { useState, useEffect } from 'react';
import {
  BrowserRouter,
  Routes,
  Route,
  Link,
} from 'react-router-dom';

import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';

import Navigation from './components/Navigation';
import Main from './components/Main';
import View from './components/View';
import Add from './components/Add';
import Update from './components/Update';


function App() {
  const [list, setList] = useState([]);
  const [addForm, setAddForm] = useState({
    description: "a"
  });

  useEffect(() => {
    document.title = 'PERN Test'
  }, []);

  useEffect(() => {
    getListFunction();
  }, []);


  const getListFunction = async (e) => {
    try {
      const response = await fetch('http://localhost:5000/todos');
      const jsonData = await response.json();

      setList(jsonData);
    } catch (err) {
      console.error(err.message);
    }
  };
  const getItemFunction = async (item_id) => {
    try {
      const response = await fetch('http://localhost:5000/todos/' + item_id);
      const jsonData = await response.json();

      setList(jsonData);
    } catch (err) {
      console.error(err.message);
    }
  };

  // console.log(list);
  const addFunction = async (e) => {
    e.preventDefault();
    // console.log(addForm.description);
    try {
      const body = {
        description: addForm.description
      };
      // console.log(body);
      const response = await fetch('http://localhost:5000/todos', {
        'method': 'POST',
        'headers': { 'Content-Type': 'application/json' },
        'body': JSON.stringify(body)
      });

      // console.log(response);
    } catch (err) {
      console.error(err.message);
    }
  };

  return (
    <>
      <BrowserRouter>
        <Routes>
          <Route path="/" element={<Navigation />}>
            <Route index element={<Main list={ list } />} />
            <Route path="/view/:id" element={<View getItemFunction={ getItemFunction } />} />
            <Route path="/add" element={<Add addFunction={ addFunction } setAddForm={ setAddForm } />} />
            <Route path="/update" element={<Update />} />
          </Route>
        </Routes>
      </BrowserRouter>
    </>
  );
}

export default App;
