import React from 'react'
import {
  Link
} from 'react-router-dom';

import Container from 'react-bootstrap/Container';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';
import Button from 'react-bootstrap/Button';


export default function NoteItem({ item, deleteItemFunction,  }) {

  const editNote = () => {
    console.log('this');
  };

  return (
    <>
      <div key={ item.todo_id } className="p-3">
        <Container fluid className="p-4 bg-warning rounded-4">
          <Row>
            <Col>
              <h5 className="fw-bold">{ item.todo_id }</h5>
            </Col>
            <Col className="text-end" xs={ 'auto' }>
              <Link className="note-close rounded-circle fw-bold text-decoration-none" as={Button} onClick={ () => deleteItemFunction(item.todo_id) }>
                X
              </Link>
            </Col>
          </Row>
          <Row>
            <Col>
              <div className="note-desc px-3 py-2" style={{ whiteSpace: 'pre-wrap' }} onClick={ editNote }>
                { item.description }
              </div>
            </Col>
          </Row>
        </Container>
      </div>
    </>
  )
}
